from pars_data import get_html
from bs4 import BeautifulSoup


brands = {}


def get_all_data_tags():
    response = get_html('https://auto.ria.com')
    soup = BeautifulSoup(response, 'html.parser')
    main_tag = soup.find('main', class_='app-content').find('div', class_='row')
    span8 = main_tag.find('div', class_='span8')
    data_tags = span8.find('form', id='mainSearchForm')
    return data_tags


